insert into organization (id, name, org_type, address_id, industry, profile, image, created_at, updated_at) values (82,'Bmc Software','COMPANY',2765,'IT & ITES','BMC is a global leader in software solutions that help IT transform traditional  businesses into digital enterprises for the ultimate competitive advantage. Our  Digital Enterprise Management set of IT solutions is designed to make digital  business fast, seamless, and optimized. From mainframe to mobile to cloud and  beyond, we pair high-speed digital innovation with robust IT  industrialization?allowing our customers to provide intuitive user experiences  with optimized performance, cost, compliance, and productivity. BMC solutions  serve more than 10,000 customers worldwide including 82 percent of the Fortune  500. ','/video/company_image/503479c4-423a-470a-963e-1aba0bd360c9.png', now(), now());
insert into organization (id, name, org_type, address_id, industry, profile, image, created_at, updated_at) values (128,'Mutual Mobile','COMPANY',2812,'IT Services','<p class="MsoListParagraph"><span style="font-size: 10pt; line-height: 115%; font-family: Arial, sans-serif; color: rgb(51, 51, 51); background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;">Innovative solutions to complex problems require carefully constructed teams. That''s why ours mix equal parts strategy, design, and engineering, with R&amp;D as a shared responsibility. Beneath this highly collaborative, iterative environment lies a passion for studying people that helps us craft experiences they love. We?ve helped many of the world?s leading brands ? including Audi, Cisco, Google, and Philips ? cut through the clutter and prioritize their technology investments to align with their brand, business objectives and users? needs.</span><o:p></o:p></p> 											 											','/video/company_image/54e9d8e4-d5b7-4b95-9597-631928202f2a.png', now(), now());
insert into organization (id, name, org_type, address_id, industry, profile, image, created_at, updated_at) values (79,'Allianz','COMPANY',2762,'FINANCIAL SERVICES','Founded in 1890 in Berlin, Allianz Group is a global leader and employer in  insurance and financial services in more than 70 countries worldwide. With  Allianz SE as headquarters, our many different insurance and financial service  companies are present on all continents. Further high quality services are  offered globally by Allianz Asset Management (Allianz Global Investors and  PIMCO), Allianz Global Corporate and Specialty (corporate and specialty risks),  Allianz Managed Operations and Services (AMOS), Euler Hermes (credit insurance)  and Allianz Worldwide Partners (assistance services).  ','/video/company_image/43ecf239-52a9-42b5-97f7-b1f9423b193f.jpg', now(), now());
