insert into organization (id, name, org_type, address_id, industry, profile, image, created_at, updated_at) values (191,'Goldman Sachs','COMPANY',2875,'FINANCIAL SERVICES',' 											<span style="color: rgba(0, 0, 0, 0.701961); font-family: &quot;Source Sans Pro&quot;, Helvetica, Arial, &quot;Noto Naskh Arabic&quot;, &quot;Noto Sans Thai&quot;, &quot;Droid Arabic Naskh&quot;, &quot;Geeza Pro&quot;, &quot;Simplified Arabic&quot;, Thonburi, Dokchampa, &quot;Droid Sans Thai&quot;, &quot;Hiragino Sans GB W3&quot;, &quot;Heiti SC&quot;, &quot;Segoe UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Droid Sans Fallback&quot;, -apple-system, &quot;.SFNSDisplay-Regular&quot;, sans-serif; font-size: 15px; white-space: pre-wrap;">At Goldman Sachs, we believe progress is everyones business. Thats why we commit our people, capital and ideas to help our clients, shareholders and the communities we serve to grow. Founded in 1869, Goldman Sachs is a leading global investment banking, securities and investment management firm. Headquartered in New York, we maintain offices in all major financial centers around the world.  </span> 											 											 											','/video/company_image/e28e1901-ff5a-4d36-afd2-6856575eece7.png', now(), now());
