insert into organization (id, name, org_type, address_id, industry, profile, image, created_at, updated_at) values (175,'CA Technologies','COMPANY',2859,'IT & ITES',' 											<span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; white-space: pre-wrap;">CA Technologies (NASDAQ: CA) helps customers succeed in a future where every business from apparel to energy is being rewritten by software. With CA software at the center of their IT strategy, organizations can leverage the technology that changes the way we live from the data center to the mobile device. Our business management software and solutions help our customers thrive in the new application economy by delivering the means to deploy, monitor and secure their applications and infrastructure. Our goal is to help organizations develop applications and experiences that excite and engage and, in turn, open up money-making opportunities for their businesses.</span> 											','/video/company_image/e3d31f21-3607-4d68-a77d-ea66fea27516.jpg', now(), now());
