insert into organization (id, name, org_type, address_id, industry, profile, image, created_at, updated_at) values (169,'Apple','COMPANY',2853,'IT & ITES',' 											<span style="color: rgba(0, 0, 0, 0.701961); font-family: &quot;Source Sans Pro&quot;, Helvetica, Arial, &quot;Noto Naskh Arabic&quot;, &quot;Noto Sans Thai&quot;, &quot;Droid Arabic Naskh&quot;, &quot;Geeza Pro&quot;, &quot;Simplified Arabic&quot;, Thonburi, Dokchampa, &quot;Droid Sans Thai&quot;, &quot;Hiragino Sans GB W3&quot;, &quot;Heiti SC&quot;, &quot;Segoe UI&quot;, &quot;Microsoft Yahei&quot;, &quot;Droid Sans Fallback&quot;, -apple-system, &quot;.SFNSDisplay-Regular&quot;, sans-serif; font-size: 15px; white-space: pre-wrap;">Apple revolutionized personal technology with the introduction of the Macintosh in 1984. Today, Apple leads the world in innovation with iPhone, iPad, the Mac and Apple Watch. Apples three software platforms  iOS, OS X and watchOS  provide seamless experiences across all Apple devices and empower people with breakthrough services including the App Store, Apple Music, Apple Pay and iCloud. Apples 100,000 employees are dedicated to making the best products on earth, and to leaving the world better than we found it. Apple is an Equal Employment Opportunity Employer that is committed to inclusion and diversity. We also take affirmative action to offer employment and advancement opportunities to all applicants, including minorities, women, protected veterans, and individuals with disabilities.</span> 											 											 											','/video/company_image/785ac06a-c965-4ca4-b370-918c5fe8ea73.png', now(), now());
insert into organization (id, name, org_type, address_id, industry, profile, image, created_at, updated_at) values (167,'Adobe','COMPANY',2851,'IT & ITES',' 											<span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; white-space: pre-wrap;">Adobe is the global leader in digital media and digital marketing solutions. Our creative, marketing and document solutions empower everyone  from emerging artists to global brands to bring digital creations to life and deliver immersive, compelling experiences to the right person at the right moment for the best results. In short, Adobe is everywhere, and we are changing the world through digital experiences.</span> 											 											 											','/video/company_image/333f4690-aa38-4864-a45d-ce49f0764921.png', now(), now());
