insert into organization (id, name, org_type, address_id, industry, profile, image, created_at, updated_at) values (223,'NCR','COMPANY',2907,'IT & ITES','<span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; white-space: pre-wrap;">NCR Corporation (NYSE: NCR) is a leader in omni-channel solutions, turning everyday interactions with businesses into exceptional experiences. With its software, hardware, and portfolio of services, NCR enables more than 600 million transactions daily across retail, financial, travel, hospitality, telecom and technology, and small business. NCR solutions run the everyday transactions that make your life easier. NCR is headquartered in Duluth, Georgia with over 30,000 employees and does business in 180 countries. NCR is a trademark of NCR Corporation in the United States and other countries. </span> 											 											<div><span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; white-space: pre-wrap;"><br></span></div>','/video/company_image/31b1d043-8058-4868-88e0-8dfa395366ec.jpg', now(), now());
