insert into organization (id, name, org_type, address_id, industry, profile, image, created_at, updated_at) values (241,'Symantec','COMPANY',2925,'IT & ITES','<span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; white-space: pre-wrap;">Symantec Corporation (NASDAQ: SYMC), the worlds leading cyber security company, helps organizations, governments and people secure their most important data wherever it lives. Organizations across the world look to Symantec for strategic, integrated solutions to defend against sophisticated attacks across endpoints, cloud and infrastructure. Likewise, a global community of more than 50 million people and families rely on Symantecs Norton suite of products for protection at home and across all of their devices. Symantec operates one of the world?s largest civilian cyber intelligence networks, allowing it to see and protect against the most advanced threats.</span> 											 											','/video/company_image/f34eaf9b-1d39-4dc9-b96e-df03fc4df55d.png', now(), now());
insert into organization (id, name, org_type, address_id, industry, profile, image, created_at, updated_at) values (233,'Synchrony Financial','COMPANY',2917,'FINANCIAL SERVICES','<span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; white-space: pre-wrap;">Synchrony Financial (NYSE: SYF) is one of the nation?s premier consumer financial services companies. Our roots in consumer finance trace back to 1932, and today we are the largest provider of private label credit cards in the United States based on purchase volume and receivables.  We provide a range of credit products through programs we have established with a diverse group of national and regional retailers, local merchants, manufacturers, buying groups, industry associations and healthcare service providers to help generate growth for our partners and offer financial flexibility to our customers.  </span> 											 											<div><span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; white-space: pre-wrap;"><br></span></div>','/video/company_image/05d32be4-e5dc-4ad8-905c-b082716565ba.jpg', now(), now());
insert into organization (id, name, org_type, address_id, industry, profile, image, created_at, updated_at) values (234,'Sierra Cedar','COMPANY',2918,'IT & ITES','<span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; white-space: pre-wrap;">Sierra-Cedar offers a comprehensive, integrated portfolio of services across multiple technologies and industries. Generally, our portfolio is categorized into industry-based consulting services and industry-agnostic shared services. Sierra-Cedar is part of Sierra-Cedar Group, Inc., one of the largest, independent North American IT services companies. Together with our Canadian affiliate, Sierra Systems Group Inc., we have approximately 2,000 professionals focused on the US and Canadian markets with global delivery capabilities in both countries and Hyderabad, India.</span> 											 											','/video/company_image/20ce84dc-befd-41e9-929d-61a119e1a2f5.png', now(), now());
