insert into organization (id, name, org_type, address_id, industry, profile, image, created_at, updated_at) values (239,'Subex','COMPANY',2923,'TELECOMMUNICATIONS','<span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; white-space: pre-wrap;">Subex Limited is a leading global provider of Business and Operations Support Systems (B/OSS) that empowers communications service providers (CSPs) to achieve competitive advantage through Business and Capex Optimisation  thereby enabling them to improve their operational efficiency to deliver enhanced service experiences to subscribers. The company pioneered the concept of a Revenue Operations Center (ROC) a centralized approach that sustains profitable growth and  financial health through coordinated operational control. Subexs product portfolio powers the ROC and its best-in-class solutions such as revenue assurance, fraud management, asset assurance, capacity management, data integrity management, credit risk management, cost management, route optimization and partner settlement. Subex also offers a scalable Managed Services program with 30 + customers. </span> 											 											<div><span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; white-space: pre-wrap;"><br></span></div>','/video/company_image/c6960a23-ab2f-4ae9-97ae-104c60d460cc.png', now(), now());
insert into organization (id, name, org_type, address_id, industry, profile, image, created_at, updated_at) values (240,'Sutherland Global Services','COMPANY',2924,'IT & ITES','<span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; white-space: pre-wrap;">Established in 1986, Sutherland Global Services is a global provider of business process and technology management services. Sutherland offers an integrated portfolio of analytics-driven back-office and customer facing solutions that support the entire customer lifecycle and is one of the largest, independent BPO companies in the world serving global leaders in major industry verticals. Headquartered in Rochester, N.Y., Sutherland employs over 38,000 professionals and has locations across the United States, Australia, Brazil, Bulgaria, Canada, China, Colombia, Egypt, Estonia, India, Jamaica, Malaysia, Mexico, Morocco, Philippines, Slovakia, Sweden, UAE, and United Kingdom.</span> 											 											','/video/company_image/eca3adce-6f35-4164-ad2c-77a25a348c97.jpg', now(), now());
insert into organization (id, name, org_type, address_id, industry, profile, image, created_at, updated_at) values (242,'Tata Technologies','COMPANY',2926,'IT & ITES','<span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; white-space: pre-wrap;">We help the worlds best manufacturers create exceptional products through better engineering and design and product development technology. Tata Technologies founded, in 1989, makes product development dreams a reality by enabling ambitious manufacturing companies to design and build better products through engineering services outsourcing and the application of information technology to product development and manufacturing enterprise processes. With over 8,500 associates, representing 28 nationalities, Tata Technologies focuses on the manufacturing industry  covering every aspect of the value chain from conceptualisation, manufacturing and aftermarket/MRO support. Tata Technologies supports clients through engineering services outsourcing, product development, IT services and product management solutions.</span> 											 											','/video/company_image/9a008234-8a58-4b31-909f-54e94aba915a.jpg', now(), now());
insert into organization (id, name, org_type, address_id, industry, profile, image, created_at, updated_at) values (243,'Tech Mahindra','COMPANY',2927,'IT & ITES','<span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; white-space: pre-wrap;">Tech Mahindra represents the connected world, offering innovative and customer-centric information technology services and solutions, enabling Enterprises, Associates and the Society to Rise. We are a USD 4 billion company with 107,200+ professionals across 90 countries, helping over 818 global customers including Fortune 500 companies. Our innovation platforms and reusable assets connect across a number of technologies to deliver tangible business value to our stakeholders. Tech Mahindra is also amongst the Fab 50 companies in Asia as per the Forbes 2015 List. We are part of the USD 17.8 billion Mahindra Group that employs more than 200,000 people in over 100 countries. The Group operates in the key industries that drive economic growth, enjoying a leadership position in tractors, utility vehicles, after-market, information technology and vacation ownership.</span> 											 											','/video/company_image/6ab1ba43-deb7-416c-aa6e-0051da287dcc.jpg', now(), now());
insert into organization (id, name, org_type, address_id, industry, profile, image, created_at, updated_at) values (244,'Texas Instruments','COMPANY',2928,'SEMICONDUCTOR','<span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; white-space: pre-wrap;">Texas Instruments (TI) is a global analog and digital semiconductor IC design and manufacturing company. In addition to analog technologies, digital signal processing (DSP) and microcontroller (MCU) semiconductors, TI designs and manufactures semiconductor solutions for analog and digital embedded, application processing, and Education Technology.</span> 											 											','/video/company_image/e4690dd0-b339-4b42-977d-aa08a009fad4.jpg', now(), now());
insert into organization (id, name, org_type, address_id, industry, profile, image, created_at, updated_at) values (245,'TIBCO Software Inc','COMPANY',2929,'IT & ITES','<span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; white-space: pre-wrap;">TIBCO Software takes businesses to their digital destinations by interconnecting everything in real time and providing augmented intelligence for everyone, from business users to data scientists. This combination delivers faster answers, better decisions, and smarter actions. For nearly 20 years, thousands of businesses around the globe have relied on TIBCO technology to differentiate themselves through compelling customer experiences, optimized assets, and innovative new business models. </span> 											 											','/video/company_image/f3b377cc-9297-48ef-aae1-f75592c27620.png', now(), now());
